import React from 'react';
import MainDrawer from './MainDrawer/MainDrawer';
import SendDrawer from './SendDrawer/SendDrawer';
import ReceiveDrawer from './ReceiveDrawer/ReceiveDrawer';
import WalletDrawer from './WalletDrawer/WalletDrawer';
import NetworkDrawer from './ReceiveDrawer/ReceiveDrawer';
import './SideDrawer.css';

export default class SideDrawer extends React.Component {
    constructor(props){
        super(props);
        let drawerClasses = [
            'side-drawer',
            'side-drawer open'
        ]
        let drawerType;
        this.state = {
            drawerClasses,
            drawerType
        }
    }

    sendCoins = () => {
        this.props.drawerType = 'send-coins'
    }

    receiveCoins = () => {
        this.props.drawerType = 'receive-coins'
    }

    changeWallet = () => {
        this.props.drawerType = 'change-wallet'
    }

    changeNetwork = () => {
        this.props.drawerType = 'change-network'
    }

    
    render() {
        let drawerRender;
        if (this.drawerType === 'main-drawer'){
            drawerRender = <MainDrawer />
        }

        if (this.drawerType === 'send-drawer'){
            drawerRender =  <SendDrawer />
        }

        if (this.drawerType === 'receive-coins'){
            drawerRender =  <ReceiveDrawer />
        }

        if (this.drawerType === 'change-wallet'){
            drawerRender =  <WalletDrawer />
        }

        if (this.drawerType === 'change-network'){
            drawerRender =  <NetworkDrawer />
        }
        return {drawerRender}
    }
}
