import React from 'react';
import SideDrawer from './SideDrawer'
import './DrawerToggle.css';

export default class DrawerToggle extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            drawerClasses: [
                'side-drawer',
                'side-drawer open'
            ],
            drawerTypes: [
                'main-drawer',
                'send-coins',
                'receive-coins',
                'change-wallet',
                'change-network',
            ]
        }
        this.setState({drawerClasses: 'side-drawer'})
        this.openDrawer = this.openDrawer.bind(this);
    }

    openDrawer = () => {
        SideDrawer.setState(drawerClasses = 'side-drawer open')
    }
    
    render (){
        return (
            <button className="toggle-btn" onClick={this.openDrawer}>
                <div className="toggle-btn-line" />
                <div className="toggle-btn-line" />
                <div className="toggle-btn-line" />
            </button>
        )
    }
}
