import React from 'react';
import './DropMenu.css';

const DropMenu = props => {
    let menuClasses = 'drop-menu'
    if(props.show){
        menuClasses = 'drop-menu open'
    }

    return (
        <div className={menuClasses}>
            <div className="drop-item">
                {props.username}
            </div>
            <div className="drop-item">
                {props.useremail}
            </div>
            <button className="drop-item" onClick={props.addWallet}>Settings</button>
            <button className="drop-item" onClick={props.removeWallet}>Tools</button>
        </div>
    )
}

export default DropMenu;
