import React from 'react';
import MainDrawer from './MainDrawer/MainDrawer';
import SendDrawer from './SendDrawer/SendDrawer';
import ReceiveDrawer from './ReceiveDrawer/ReceiveDrawer';
import WalletDrawer from './WalletDrawer/WalletDrawer';
import NetworkDrawer from './ReceiveDrawer/ReceiveDrawer';
import './SideDrawer.css';

export default class SideDrawer extends React.Component {
    constructor(props){
        super(props);
        let drawerClasses = [
            'side-drawer',
            'side-drawer open'
        ]
        this.state = {drawerClasses}
    }
    
    render() {
        let drawerType = [];

        const sendCoins = () => {
            drawerType = 'send-coins'
        }

        const receiveCoins = () => {
            drawerType = 'receive-coins'
        }

        const changeWallet = () => {
            drawerType = 'change-wallet'
        }

        const changeNetwork = () => {
            drawerType = 'change-network'
        }

        if (!drawerType){
            return <MainDrawer />
        }

        if (drawerType === 'send-drawer'){
            return <SendDrawer />
        }

        if (drawerType === 'receive-coins'){
            return <ReceiveDrawer />
        }

        if (drawerType === 'change-wallet'){
            return <WalletDrawer />
        }

        if (drawerType === 'change-network'){
            return <NetworkDrawer />
        }
    }
}
