import React from 'react';

import './DrawerToggle.css';

export default class DrawerToggle extends React.Component {
    constructor(props){
        super(props);
        let drawerClasses = [
            'side-drawer',
            'side-drawer open'
        ];
        this.state = {drawerClasses}
    }

    handleMenuClick = () => {
        this.setState({drawerClasses:!this.state.drawerClasses})
    }

    render (){
        return (
            <button className="toggle-btn" onClick={this.handleMenuClick}>
                <div className="toggle-btn-line" />
                <div className="toggle-btn-line" />
                <div className="toggle-btn-line" />
            </button>
        )
    }
}
