import React from 'react';
import MainDrawer from './MainDrawer/MainDrawer';
import SendDrawer from './SendDrawer/SendDrawer';
import ReceiveDrawer from './ReceiveDrawer/ReceiveDrawer';
import WalletDrawer from './WalletDrawer/WalletDrawer';
import NetworkDrawer from './ReceiveDrawer/ReceiveDrawer';
import './SideDrawer.css';

const SideDrawer = props => {
    let drawerClasses = 'side-drawer';
    if(props.show){
        drawerClasses = 'side-drawer open';
    }

    const drawerChoice = () => {
        if (props.drawerTypes === 'main-drawer'){
            return <MainDrawer drawerClasses={props.drawerClasses}/>
        }
        if (props.drawerTypes === 'send-drawer' && this.props.show){
            props.drawerClasses = ['side-drawer', 'open']
            return <SendDrawer drawerClasses={this.drawerClasses}/>
        }
        if (props.drawerTypes === 'receive-coins' && this.props.show){
            props.drawerClasses = ['side-drawer', 'open']
            return <ReceiveDrawer drawerClasses={this.drawerClasses}/>
        }
        if (props.drawerTypes === 'change-wallet' && this.props.show){
            props.drawerClasses = ['side-drawer', 'open']
            return <WalletDrawer drawerClasses={this.drawerClasses}/>
        }
        if (props.drawerTypes === 'change-network' && this.props.show){
            props.drawerClasses = ['side-drawer', 'open']
            return <NetworkDrawer drawerClasses={this.drawerClasses}/>
            }
        }
    return (
        drawerChoice
    )
}

export default SideDrawer;
