import React from 'react';
import MainDrawer from './MainDrawer/MainDrawer';
import SendDrawer from './SendDrawer/SendDrawer';
import ReceiveDrawer from './ReceiveDrawer/ReceiveDrawer';
import WalletDrawer from './WalletDrawer/WalletDrawer';
import NetworkDrawer from './ReceiveDrawer/ReceiveDrawer';
import './SideDrawer.css';

export default class SideDrawer extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            drawerClasses: ['side-drawer']
        }
    }

    render() {
        const drawerChoice = () => {
            if (this.drawerTypes === 'main-drawer' && this.props.show){
                this.drawerClasses = 'side-drawer open'
                return <MainDrawer drawerClasses={this.drawerClasses}/>
            }
            if (this.drawerTypes === 'send-drawer' && this.props.show){
                this.drawerClasses = 'side-drawer open'
                return <SendDrawer drawerClasses={this.drawerClasses}/>
            }
            if (this.drawerTypes === 'receive-coins' && this.props.show){
                this.drawerClasses = 'side-drawer open'
                return <ReceiveDrawer drawerClasses={this.drawerClasses}/>
            }
            if (this.drawerTypes === 'change-wallet' && this.props.show){
                this.drawerClasses = 'side-drawer open'
                return <WalletDrawer drawerClasses={this.drawerClasses}/>
            }
            if (this.drawerTypes === 'change-network' && this.props.show){
                this.drawerClasses = 'side-drawer open'
                return <NetworkDrawer drawerClasses={this.drawerClasses}/>
            }
        }
        return (
            {drawerChoice}
        )
    }
}
