import React from 'react';
import MainDrawer from './MainDrawer/MainDrawer';
import SendDrawer from './SendDrawer/SendDrawer';
import ReceiveDrawer from './ReceiveDrawer/ReceiveDrawer';
import WalletDrawer from './WalletDrawer/WalletDrawer';
import NetworkDrawer from './ReceiveDrawer/ReceiveDrawer';
import './SideDrawer.css';

const SideDrawer = props => {
    let drawerClasses = 'side-drawer';
    if(props.show){
        drawerClasses = 'side-drawer open';
    }

    const drawerChoice = () => {
        if (this.drawerType === 'main-drawer'){
            return <MainDrawer
                drawerClasses={drawerClasses}
                sendCoins={this.sendCoins}
                receiveCoins={this.receiveCoins}
                changeWallet={this.changeWallet}
                changeNetwork={this.changeNetwork}/>
        }
        if (this.drawerType === 'send-drawer'){
            return <SendDrawer drawerClasses={drawerClasses}/>
        }
        if (this.drawerType === 'receive-coins'){
            return <ReceiveDrawer drawerClasses={drawerClasses}/>
        }
        if (this.drawerType === 'change-wallet'){
            return <WalletDrawer drawerClasses={drawerClasses}/>
        }
        if (this.drawerType === 'change-network'){
            return <NetworkDrawer drawerClasses={drawerClasses}/>
            }
        }
        
    return <MainDrawer 
        drawerClasses={drawerClasses}
        sendCoins={this.props.sendCoins}
        receiveCoins={this.receiveCoins}
        changeWallet={this.changeWallet}
        changeNetwork={this.changeNetwork}/>
}

export default SideDrawer;
