import React from 'react';
import MainDrawer from './MainDrawer/MainDrawer';
import SendDrawer from './SendDrawer/SendDrawer';
import ReceiveDrawer from './ReceiveDrawer/ReceiveDrawer';
import WalletDrawer from './WalletDrawer/WalletDrawer';
import NetworkDrawer from './ReceiveDrawer/ReceiveDrawer';
import './SideDrawer.css';

export default class SideDrawer extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            drawerClasses: [
                'side-drawer',
                'side-drawer open'
            ],
            drawerType: [
                'main-drawer',
                'send-coins',
                'receive-coins',
                'change-wallet',
                'change-network',
            ]
        }
    }
    
    handleMenuClick = () => {
        SideDrawer.props.drawerClasses = 'side-drawer open'
    }

    sendCoins = () => {this.setState.drawerType = 'send-coins'}
    receiveCoins = () => {this.setState.drawerType = 'receive-coins'}
    changeWallet = () => {this.setState.drawerType = 'change-wallet'}
    changeNetwork = () => {this.setState.drawerType = 'change-network'}
    
    render() {
        const drawerChoice = () => {
            if (this.drawerType === 'main-drawer'){
                return <MainDrawer />
            }
            if (this.drawerType === 'send-drawer'){
                return <SendDrawer />
            }
            if (this.drawerType === 'receive-coins'){
                return <ReceiveDrawer />
            }
            if (this.drawerType === 'change-wallet'){
                return <WalletDrawer />
            }
            if (this.drawerType === 'change-network'){
                return <NetworkDrawer />
            }
        }

        return (
            <div>{drawerChoice}</div>
        )
    }
}
