import React from 'react';
import SendDrawer from '../SendDrawer/SendDrawer'
import './SideDrawer.css';

export default class SideDrawer extends React.Component {
    constructor(props){
        super(props);
        let drawerClasses = [
            'side-drawer',
            'side-drawer open'
        ]
        this.state = {drawerClasses}

    }
    
    render() {
        let drawerType = [];

        const sendCoins = () => {
            drawerType = 'send-coins'
        }

        const receiveCoins = () => {
            drawerType = 'receive-coins'
        }

        const changeWallet = () => {
            drawerType = 'change-wallet'
        }

        const changeNetwork = () => {
            drawerType = 'change-network'
        }

        if (!drawerType){
            return (
                <nav className={this.state.drawerClasses}>
                    <div className="company-name">
                        KralCo
                    </div>
                    <ul>
                        <li><button onClick={this.sendCoins}>Send Coins</button></li>
                        <li><button onClick={this.receiveCoins}>Receive Coins</button></li>
                        <li><button onClick={this.changeWallet}>Change Wallet</button></li>
                        <li><button onClick={this.changeNetwork}>Change Network</button></li>
                    </ul>
                </nav>
            )
        }

        if (drawerType === 'send-drawer'){
            return (
                <SendDrawer />
            )
        }
    }
}
