import React, {Component} from 'react';
import Toolbar from './components/Toolbar/Toolbar';
import SideDrawer from './components/SideDrawer/SideDrawer';
import Backdrop from './components/Backdrop/Backdrop';
import DataDivs from './components/DataDivs/DataDivs';
import './App.css'

export default class App extends Component {
  constructor(props){
      super(props);
      this.state = {
          drawerClasses: '',
          drawerTypes: [
              'main-drawer',
              'send-coins',
              'receive-coins',
              'change-wallet',
              'change-network',
          ]
      }
      this.setState({drawerClasses: 'side-drawer'})
      this.openDrawer = this.openDrawer.bind(this);
  }

  openDrawer = () => {
      this.setState({drawerClasses: 'side-drawer open'})
  }
  
  render() {
        
    return (
      <div className="App">
        <div style={{height: '100%'}}>
          <Toolbar
            drawerClasses={this.state.drawerClasses}
            drawerTypes={this.state.drawerTypes}/>
          <SideDrawer
            openDrawer={this.openDrawer}
            drawerClasses={this.state.drawerClasses}
            drawerTypes={this.state.drawerTypes}/>
          {Backdrop.backdrop}
        </div>
        <div className="App-content">
          <div className="dollar-value">
            <h4>Total USD Balance</h4>
            <h2>$20,000.00</h2>
          </div>
          <DataDivs />
        </div>
      </div>
    );
  }
}
