import React from 'react';
import './ReceiveDrawer.css';

const ReceiveDrawer = props => {
    return (
        <div className="rec-container">
            <button className="back-button" onClick={props.backbtn}>
                Back
            </button>
            <form className="receive-drawer">
                <div>
                    <div className="rec-item">
                        Network: {props.fromAddress}
                    </div>
                    <div className="rec-item">
                        Coin Type:
                        <select className="send-select" name="send-name">
                            <option defaultValue>Select Coin Type</option>
                            {Object.keys(props.coins).map((key) => (
                                <option>{key}</option>
                            ))}
                        </select>
                    </div>
                    <div className="rec-item">
                        Amount: {props.coinAmount}
                    </div>
                </div>
                <div className="rec-submit-container">
                    <input className="rec-submit" type="submit" value="OK"/>
                </div>
            </form>
        </div>
    )
}

export default ReceiveDrawer;
