import React from 'react';
import bitcoin from '../images/bitcoin.png';
import litecoin from '../images/litecoin.png';
import ethereum from '../images/ethereum.png';
import 'DataDivs.css';

class DataDivs extends React.Component {
    render() {
        var coins = [
            {name: "Bitcoin", icon: <img src={bitcoin} alt="" />, amount: 100, value: this.amount * 6000},
            {name: "Lightcoin", icon: <img src={litecoin} alt="" />, amount: 200, value: this.amount * 500},
            {name: "Ethereum", icon: <img src={ethereum} alt="" />, amount: 1000, value: this.amount * 30}
        ]
        return (
            <div className="coin-list">
                <div className="list-header">
                    <div className="header-name">Name</div>
                    <div className="header-amount">Amount</div>
                    <div className="header-value">Value</div>
                </div>
                <div className="list-body">
                    <div className="table-row">
                        {this.props.coins.map((icon, name, i) => <div className="row-name" key={i}>{icon}{name}</div>)}
                        <div className="row-amount">1.0</div>
                        <div className="row-value">$3,000</div>
                    </div>
                    <div className="table-row">
                        <div className="row-name"><img src={litecoin} alt="" />Litecoin</div>
                        <div className="row-amount">2.0</div>
                        <div className="row-value">$2,000</div>
                    </div>
                    <div className="table-row">
                        <div className="row-name"><img src={ethereum} alt="" />Ethereum</div>
                        <div className="row-amount">3.0</div>
                        <div className="row-value">$1,000</div>
                    </div>
                </div>
            </div>
        )
    }
}


export default DataDivs;
