import React from 'react';
import './DataDivs.css';
var NumberFormat = require('react-number-format');

const DataDivs = props => {
  return (
    <div className="coin-list">
      <div className="table-row">
        {Object.keys(props.coins).map((key) => (
          <div id="crypto-container">
            <span className="left"><img src={props.coins[key].USD.ImageUrl} alt={`${key} icon`} /></span>
            <span className="left">{key}</span>
            <span className="right"><NumberFormat value={props.coins[key].USD.PRICE} displayType={'text'} decimalprecision={2} thousandSeparator={true} prefix={'$'} /></span>
          </div>
        ))}
      </div>
    </div>
  )
}

export default DataDivs;
